##############################################################################
#   Installs OpenVnmrJ on Ubuntu or Alma using Windows Subsystem for Linux   #
#                                                                            #
#  To run this script:                                                       #
#                                                                            #
#      * Right-click on this file and select Properties                      #
#          * Check the Unblock box at the bottom if present and click OK     #
#                                                                            #
#      * Right-click on this file and select Run with PowerShell             #
#                                                                            #
#   Troubleshooting see: https://github.com/JessiBaughman/OpenVnmrJ_WSL      #
#                                                                            #
##############################################################################

# Last update: September 16, 2024

#########################
###   Configuration   ###
#########################

$defaultWSL = "Ubuntu-24.04" # which WSL distro to use/install by default
$supportedWSL="Ubuntu","Ubuntu-20.04","Ubuntu-22.04","Ubuntu-24.04","AlmaLinux-9","AlmaLinux-8"
$defaultWSLlink = "https://aka.ms/wslubuntu" # link to default distro (See: https://docs.microsoft.com/en-us/windows/wsl/install-manual)
$minDist = 20 # minimum int version of Ubuntu supported
$minAlma = 8 # minimum int version of Alma Linux supported
$wslgBuild = 19044 # First build of Windows to support WSLg
$ovjWSLfolder="$([Environment]::GetFolderPath('UserProfile'))\Downloads\OpenVnmrJ_WSL_install" # working directory for downloads

#############################
###   End Configuration   ###
#############################

# Load for display message boxes and form
[void][System.Reflection.Assembly]::Load('System.Drawing, Version=4.0.0.0, Culture=neutral, PublicKeyToken=b03f5f7f11d50a3a')
[void][System.Reflection.Assembly]::Load('System.Windows.Forms, Version=4.0.0.0, Culture=neutral, PublicKeyToken=b77a5c561934e089')

# Determine if on Windows 10 Build 19041 or higher for WSL 2 support
$winBuild = [int](Get-CimInstance -Class Win32_OperatingSystem | Select-Object -ExpandProperty BuildNumber)
if ( $winBuild -lt 19041 ) {
    [void][System.Windows.Forms.MessageBox]::Show( "This installer is only compatible with `nWindows 10 version 2004 (Build 19041) or higher.`n`nPlease upgrade your system and try again.", "Windows OS Update Required", "Ok", "Hand" )
    Exit 1
}

$enableFeatures = $false
$revertWSLdefault = $false
# Check (without elevation) if WSL is enabled - Get-WindowsOptionalFeature requires elevation and can stall
#     If disabled, wsl --help will only show a limited set of options
if ('' -eq (((wsl --help) -replace "`0" | Select-String --status) -replace " ")) {$enableFeatures=$true}
else { # WSL enabled, check VirtualMachinePlatform (without elevation)
    if ((((wsl --status) -replace "`0" | Select-String "Default Version") -split ": ")[1] -eq 1) {
        # Check if default is 1 to restore later
        $revertWSLdefault = $true
    }
    # Temporarily change default WSL install version to 2
    #     Speeds up install & Vnmrbg won't run in WSL1
    if (((wsl --set-default-version 2) -replace "`0" | Select-String "Virtual") -match "Virtual") {$enableFeatures=$true}
}
if ($enableFeatures) {
    if (-Not ([Security.Principal.WindowsPrincipal] [Security.Principal.WindowsIdentity]::GetCurrent()).IsInRole([Security.Principal.WindowsBuiltInRole] 'Administrator')) {
        if ([System.Windows.Forms.MessageBox]::Show( "Windows Subsystem for Linux and Virtual Machine Platform features need to be enabled to install OpenVnmrJ.`n`nNote:`nYour computer will automatically restart after these features are enabled.`nAfter restarting, run this script again to continue the OpenVnmrJ installation.`n`nDo you want to continue?", "Pre-Install: Enable Windows Features", "YesNo", "Warning" ) -eq 'Yes' ) {
            # Prompt for admin elevation to enable the features - this also restarts the script
            if ($winBuild -ge 6000) {
                $CommandLine = "-File `"" + $MyInvocation.MyCommand.Path + "`" " + $MyInvocation.UnboundArguments
                Start-Process -FilePath PowerShell.exe -Verb Runas -ArgumentList $CommandLine
                Exit
            }
        }
        else {Exit 1} # User selected No
    }
    else { # Skip prompt and install features
        echo "Enabling Windows Subsystem for Linux"
        Enable-WindowsOptionalFeature -Online -FeatureName Microsoft-Windows-Subsystem-Linux -All -NoRestart -WarningAction SilentlyContinue
        echo "Enabling Virtual Machine Platform"
	    Enable-WindowsOptionalFeature -Online -FeatureName VirtualMachinePlatform -All -NoRestart -WarningAction SilentlyContinue
        echo "";echo "*** Complete!  ***";echo ""
        Read-Host -Prompt "Press [Enter] to restart this computer"
        Restart-Computer -Force
        Exit 0
    }
}    

