$selectInstaller = {
	Add-Type -AssemblyName System.Windows.Forms
	$initialDirectory = [Environment]::GetFolderPath('UserProfile') + '\Downloads'
	$OpenFileDialog = New-Object System.Windows.Forms.OpenFileDialog
	$OpenFileDialog.InitialDirectory = $initialDirectory
	$OpenFileDialog.Filter = 'Archive files (*.zip;*.tar;*.tar.gz)|*.zip;*.tar;*.tar.gz'
	$OpenFileDialog.Multiselect = $false
	$response = $OpenFileDialog.ShowDialog( ) # $response can return OK or Cancel
	if ( $response -eq 'OK' ) { 
		$installerTextBox.Text =  $OpenFileDialog.FileName 
