# Install and configure Ubuntu or Alma-based Linux in WSL
function installUbuntu([string]$distribution) {
    
    $wslExe=$(-join(($distribution -replace "-" -replace "\." -replace "_").ToLower(),".exe"))
    if ((echo $distribution | Select-String Alma)) {
        $alversion = (($distribution -split "\.")[0] -replace "Alma.*-")
        getWinget
        winget install -q "AlmaLinux OS $alversion" -s msstore --accept-package-agreements
        Start-Process "shell:AppsFolder\$((Get-StartApps "AlmaLinux OS $alversion" | Select-Object -First 1).AppId)"
        Sleep 1
        Stop-Process (Get-Process "AlmaLinux$alversion").Id
    } else {
        wsl --install -d $distribution --no-launch
        #[void](taskkill /IM $wslExe /F)
    }
    wsl --shutdown
    echo "Starting WSL install"
    $cmd = -join($wslExe,' install --root')
    Invoke-Expression $cmd # Finish setup and set root as default user (for now)
    echo ""
    # Verify install
    if (((wsl -d $distribution echo "installed") -replace "`0") -match "no distribution") {
        [void][System.Windows.Forms.MessageBox]::Show( "Windows Subsystem for Linux failed to install. Verify virtualization is enabled in the BIOS and then rerun this script.", "Pre-Install: Enable Windows Features", "OK", "Hand")
        Exit 1
    }
    $cmd = -join('useradd --create-home --shell /bin/bash --comment "', $userNameTextBox.Text, '" ', $userNameTextBox.Text)
    wsl -d $distribution -u root /bin/bash -c $cmd # add user
    $cmd = -join("echo ",$userNameTextBox.Text,":",$passwordTextBox.Text," | chpasswd") # use join due to variable names and : character
    wsl -d $distribution -u root /bin/bash -c $cmd # set user password
    Write-Host "Setting",$userNameTextBox.Text,"as default user"
    $cmd = -join($wslExe," config --default-user ",$userNameTextBox.Text)
    Invoke-Expression $cmd # set user as default
    echo ""
}

# Configure Ubuntu
function setupUbuntu([string]$distribution) {
    if ((echo $distribution | Select-String Ubuntu)) {
        $pkgMan = "apt"
        $admin="admin"
    } else {
        $pkgMan = "dnf"
        $admin="wheel"
    }
    wsl -d $distribution -u root /bin/bash -c "groupadd nmr"
    Write-Host "Add",$userNameTextBox.Text,"to nmr and admin group"
    wsl -d $distribution -u root usermod --append --groups nmr,$admin $userNameTextBox.Text
    $nmrid = (wsl -d $distribution -u root grep nmr:x /etc/group).split(':')[2]
    echo ""; Write-Host "Adding vnmr1:nmr user (same password as",$userNameTextBox.Text,")"
    wsl -d $distribution -u root useradd --create-home --shell /bin/bash --comment 'vnmr1' --gid $nmrid vnmr1
    $cmd = -join("echo vnmr1:",$passwordTextBox.Text," | chpasswd")
    wsl -d $distribution -u root /bin/bash -c $cmd # set vnmr1 password
    wsl -d $distribution -u root usermod --append --groups $admin vnmr1
    echo ""
    
    # Convert distribution to WSL 2 if currently WSL 1 (Vnmrbg won't run in WSL 1)
    if ((((wsl -l -v) -replace "`0" | Select-String -SimpleMatch "$distribution ") -replace '.* ') -eq 1) {
        echo "Converting $distribution to WSL 2"
        wsl --set-version $distribution 2
        echo ""
    }

	echo "Configuring $distribution";echo ""

    echo "Disabling SELinux"
    if ($(wsl -d $distribution -u root --cd /tmp /bin/bash -c "[ -f /etc/selinux/config ] && echo exists || echo")) {
        wsl -d $distribution -u root /bin/bash -lic "sed -i 's/SELINUX=[eE][nN][fF][oO][rR][cC][iI][nN][gG]/SELINUX=disabled/' /etc/selinux/config; sed -i 's/SELINUX=[pP][eE][rR][mM][iI][sS][sS][iI][vV][eE]/SELINUX=disabled/' /etc/selinux/config"
    } else {
        wsl -d $distribution -u root /bin/bash -lic "echo 'SELINUX=disabled' > /etc/selinux/config"
    }
    wsl --shutdown # reboot
    echo ""

    echo "Updating $distribution"
    if ((echo $pkgMan | Select-String apt)) {wsl -d $distribution -u root /bin/bash -lic "apt update;apt upgrade -y"}
    # Add EPEL and create dnf/yum link
    if ((echo $pkgMan | Select-String dnf)) {
        wsl -d $distribution -u root /bin/bash -lic "ln -s /bin/dnf /bin/yum"
        wsl -d $distribution -u root /bin/bash -lic "dnf install -y epel-release"
        wsl -d $distribution -u root /bin/bash -lic "/usr/bin/crb enable"
        wsl -d $distribution -u root /bin/bash -lic "dnf upgrade -y"
    }

    # Enable systemd if needed
    wsl -d $distribution -u root /bin/bash -lic 'if [ ! -f /etc/wsl.conf ]; then echo "[boot]" > /etc/wsl.conf; echo "systemd=true" >> /etc/wsl.conf; fi; if [ $(grep -c "[boot]" /etc/wsl.conf) -lt 1 ]; then echo "[boot]" >> /etc/wsl.conf; fi; if [ $(grep -c "systemd" /etc/wsl.conf) -gt 0 ]; then sed -i "s/systemd=.*/systemd=true/g" /etc/wsl.conf; else sed -i "/boot/asystemd=true" /etc/wsl.conf; fi '
    wsl --shutdown # "reboot"
    echo ""
    
    echo "Installing cups printer service"
    # Install, start, and enable no-password cups start on boot first. Needed to properly install printer-driver-cups-pdf 
    wsl -d $distribution -u root /bin/bash -lic "$pkgMan install -y cups;/usr/bin/systemctl enable --now cups"
    wsl --shutdown # "reboot"

    echo "";echo "Installing PDF viewer, Firefox, htop, unzip, and neofetch"
    wsl -d $distribution -u root /bin/bash -lic "$pkgMan install -y okular firefox htop unzip neofetch;$pkgMan autoremove -y"
    echo ""
    
    echo "Set Okular as default PDF viewer"
    # Default user
    wsl -d $distribution --cd ~ /bin/bash -c "[ -d .config ] && echo || mkdir .config"
    wsl -d $distribution --cd ~ /bin/bash -c "[ -f .config/mimeapps.list ] && echo || echo > .config/mimeapps.list"
    if ((wsl -d $distribution --cd ~ /bin/bash -c "grep -c '\[Default Applications\]' ~/.config/mimeapps.list") -eq 0) { # Don't add if already present
        wsl -d $distribution --cd ~ sed -i '1i\[Default Applications]' ~/.config/mimeapps.list
    }
    if ((wsl -d $distribution /bin/bash -c "sed -n '/\[Default Applications\]/,/\[Added Associations\]/p' ~/.config/mimeapps.list | grep -c 'application/pdf=okularApplication_pdf.desktop;'") -eq 0) {
        wsl -d $distribution --cd ~ sed -i '/\[Default Applications\]/a application/pdf=okularApplication_pdf.desktop;\n' ~/.config/mimeapps.list
    }
    if ((wsl -d $distribution --cd ~ /bin/bash -c "grep -c '\[Added Associations\]' ~/.config/mimeapps.list") -eq 0) { # Don't add if already present
        wsl -d $distribution --cd ~ sed -i '$ a\[Added Associations]' ~/.config/mimeapps.list
    }
    if ((wsl -d $distribution /bin/bash -c "sed -n '/\[Added Associations\]/,//p' ~/.config/mimeapps.list | grep -c 'application/pdf=okularApplication_pdf.desktop;'") -eq 0) {
        wsl -d $distribution --cd ~ sed -i '/\[Added Associations\]/a application/pdf=okularApplication_pdf.desktop;\n' ~/.config/mimeapps.list
    }
    # vnmr1
    wsl -d $distribution -u vnmr1 --cd ~ /bin/bash -c "[ -d .config ] && echo || mkdir .config"
    wsl -d $distribution -u vnmr1 --cd ~ /bin/bash -c "[ -f .config/mimeapps.list ] && echo || echo > .config/mimeapps.list"
    if ((wsl -d $distribution -u vnmr1 --cd ~ /bin/bash -c "grep -c '\[Default Applications\]' ~/.config/mimeapps.list") -eq 0) { # Don't add if already present
        wsl -d $distribution -u vnmr1 --cd ~ sed -i '1i\[Default Applications]' ~/.config/mimeapps.list
    }
    if ((wsl -d $distribution -u vnmr1 /bin/bash -c "sed -n '/\[Default Applications\]/,/\[Added Associations\]/p' ~/.config/mimeapps.list | grep -c 'application/pdf=okularApplication_pdf.desktop;'") -eq 0) {
        wsl -d $distribution -u vnmr1 --cd ~ sed -i '/\[Default Applications\]/a application/pdf=okularApplication_pdf.desktop;\n' ~/.config/mimeapps.list
    }
    if ((wsl -d $distribution -u vnmr1 --cd ~ /bin/bash -c "grep -c '\[Added Associations\]' ~/.config/mimeapps.list") -eq 0) { # Don't add if already present
        wsl -d $distribution -u vnmr1 --cd ~ sed -i '$ a\[Added Associations]' ~/.config/mimeapps.list
    }
    if ((wsl -d $distribution -u vnmr1 /bin/bash -c "sed -n '/\[Added Associations\]/,//p' ~/.config/mimeapps.list | grep -c 'application/pdf=okularApplication_pdf.desktop;'") -eq 0) {
        wsl -d $distribution -u vnmr1 --cd ~ sed -i '/\[Added Associations\]/a application/pdf=okularApplication_pdf.desktop;\n' ~/.config/mimeapps.list
    }
    echo ""

    echo "Add PDF, Documents, and Downloads folder symbolic links"
    $pdfFolder=$(wsl -d $distribution wslpath "$($pdfPrinterTextBox.Text) ") # needs trailing space to work
    $pdfFolder=$pdfFolder.Trim(' ') # get rid of trailing space
    wsl -d $distribution ln -s "$pdfFolder" '$HOME/PDF'
    $dlfolder=$(wsl -d $distribution wslpath "$([Environment]::GetFolderPath('UserProfile'))\Downloads ")
    $dlfolder=$dlfolder.Trim(' ')
    wsl -d $distribution ln -s "$dlfolder" '$HOME/Downloads'
    $docFolder=$(wsl -d $distribution wslpath "$([Environment]::GetFolderPath('MyDocuments')) ")
    $docFolder=$docFolder.Trim(' ')
    wsl -d $distribution ln -s "$docFolder" '$HOME/Documents'
    echo ""

    echo "Setup vimrc file"
    # Create/Reset vimrc additions
    wsl -d $distribution -u root --cd ~ /bin/bash -c "[ -f .vimrc ] && echo || echo > .vimrc"
    wsl -d $distribution /bin/bash -c "touch ~/.vimrc"
    wsl -d $distribution -u vnmr1 /bin/bash -c "touch ~/.vimrc"
    wsl -d $distribution -u root --cd ~ /bin/bash -lic "sed -i '/AddedByOVJInstaller/d' .vimrc /home/*/.vimrc"
    wsl -d $distribution -u root --cd ~ sed -i '$ a\syntax on \"AddedByOVJInstaller' /root/.vimrc # Ensure syntax highlighting is on
    wsl -d $distribution -u root --cd ~ sed -i '$ a\set bg=dark \"AddedByOVJInstaller' /root/.vimrc # Adjust colors for dark background
    wsl -d $distribution -u root --cd ~ sed -i '$ a\set mouse=a \"AddedByOVJInstaller' /root/.vimrc # Enable mouse scrolling
    [void](wsl -d $distribution -u root /bin/bash -c "tail -n 3 /root/.vimrc | tee -a /home/*/.vimrc") # Append vim settings for all users

    # Add script to (un)mount Windows drives in Linux
    echo "Adding winmount script"
    wsl -d $distribution -u root /bin/bash -c "curl https://pastebin.com/raw/4pjbQmBN > /usr/bin/winmount;chmod +x /usr/bin/winmount"
    echo ""

    # Setup DISPLAY environment variable and Xauthority settings for Windows 10
    #      - not needed for Windows 11 (and any future Win10 builds with WSLg)
    if ($winBuild -lt $wslgBuild) {
        echo "Configure DISPLAY and X Server settings"
        # Delete lines if settings already present (e.g. rerunning script)
        wsl -d $distribution -u root sed -i '/AddedByOVJInstaller/d' /root/.profile 
        wsl -d $distribution -u root sed -i '/AddedByOVJInstaller/d' /home/*/.profile

        # Set $DISPLAY - If WSL2, this will be different on different networks
        wsl -d $distribution -u root sed -i '$ a\ ' /root/.profile
        wsl -d $distribution -u root sed -i '$ a\# In WSL 2, DISPLAY must be the Windows network IP, not localhost #AddedByOVJInstaller' /root/.profile
        wsl -d $distribution -u root sed -i '$ a\# Execute route.exe in Windows to determine its IP address #AddedByOVJInstaller' /root/.profile
        $cmd = 'sed -i \"$ a\export DISPLAY=\\\$(/mnt/c/WINDOWS/system32/route.exe print | grep 0.0.0.0 | head -1 | awk '+"'"+'{print \\\$4}'+"'"+'):0.0 #AddedByOVJInstaller\" /root/.profile' # quote and $ escape magic
        wsl -d $distribution -u root /bin/bash -c "$cmd"
        echo ""

        # Update Xauthority if full hostname/network changes
        #    - xauth overwrites links, so the file can't be linked to the Windows location / must be copied
        echo "Configure .Xauthority for X server"
        $magiccookie=$(wsl -d $distribution /bin/bash -c "echo '$user$pass'|tr -d '\n\r'|md5sum").split(' ')[0]
        $winXauthority=$(wsl -d $distribution wslpath "$([Environment]::GetFolderPath('UserProfile'))\.Xauthority ")
        wsl -d $distribution -u root sed -i '$ a\ ' /root/.profile
        wsl -d $distribution -u root sed -i '$ a\# Update Xauthority if needed and copy to Windows for X server #AddedByOVJInstaller' /root/.profile
        # First pull current Windows file to avoid loosing info (e.g. if running more than one WSL distribution)
        wsl -d $distribution -u root sed -i '$ a\xauth merge \$(wslpath \$(/mnt/c/Windows/System32/cmd.exe /C \"echo \%USERPROFILE\%\"  2>/dev/null | tr -d \"\\\r\\\n\"))/.Xauthority #AddedByOVJInstaller' ~/.profile
        $cmd="sed -i '$ a\xauth add "+'\"\$DISPLAY\"'+" . "+'\"'+"$magiccookie"+'\"'+" #AddedByOVJInstaller' /root/.profile"
        wsl -d $distribution -u root /bin/bash -c "$cmd" # Will only add if missing / won't insert duplicates
        # Copy file back to Windows
        wsl -d $distribution -u root sed -i '$ a\cp ~/.Xauthority \$(wslpath \$(/mnt/c/Windows/System32/cmd.exe /C \"echo \%USERPROFILE\%\"  2>/dev/null | tr -d \"\\\r\\\n\"))/.Xauthority #AddedByOVJInstaller' ~/.profile
        
        # Create .Xauthority files to supress error on first login
        wsl -d $distribution -u root --cd ~ touch .Xauthority
        wsl -d $distribution -u vnmr1 --cd ~ touch .Xauthority
        wsl -d $distribution --cd ~ touch .Xauthority

        [void](wsl -d $distribution -u root /bin/bash -c "tail -n 9 /root/.profile | tee -a /home/*/.profile") # Add DISPLAY/Xauthority settings for all users
        echo ""
    }
    wsl --shutdown # "reboot"

    echo "Installing PDF printer"
    if ((echo $pkgMan | Select-String apt)) {$cupspdf="printer-driver-cups-pdf"} else {$cupspdf="cups-pdf"}
    wsl -d $distribution -u root /bin/bash -lic "systemctl enable --now cups;$pkgMan install -y $cupspdf"
    wsl --shutdown
    wsl -d $distribution /bin/bash -lic "echo ' '"
    wsl -d $distribution -u root sed -i 's/^Out.*/Out \$\{HOME\}\/PDF/' /etc/cups/cups-pdf.conf
    # Need a delay after lpadmin to get /etc/cups/printers.conf to be written from outside a WSL window
    if ((echo $pkgMan | Select-String apt)) {
        wsl -d $distribution -u root /bin/bash -lic "lpadmin -p cups-pdf -v cups-pdf:/ -E -P /usr/share/ppd/cups-pdf/CUPS-PDF_opt.ppd; sleep 30"
    } else {
        wsl -d $distribution -u root /bin/bash -lic "lpadmin -p cups-pdf -v cups-pdf:/ -E -P /usr/share/cups/model/CUPS-PDF_opt.ppd; sleep 30"
    }
    echo ""
}

# Run the OpenVnmrJ installer and configure 
function installOVJ([string]$distribution) {
    if ($installerRadioButton2.Checked) {$ovjInstaller = $installerTextBox.Text} # Use own installer
    else { # Download latest release
        # Get link for current DDR release
        $latestOVJver = ((Invoke-WebRequest "https://github.com/OpenVnmrJ/OpenVnmrJ/releases/latest").Links.Href | Select-String "/OpenVnmrJ/OpenVnmrJ/releases/tag").ToString().split('/')[-1]
        $latestOVJ = "https://github.com"+ ((Invoke-WebRequest "https://github.com/OpenVnmrJ/OpenVnmrJ/releases/expanded_assets/$latestOVJver").Links.Href | Select-String "/OpenVnmrJ/OpenVnmrJ/releases/download/OpenVnmrJ.*DDR.zip")
        # Set installer file path
        $ovjInstaller = "$ovjWSLfolder\" + [System.IO.Path]::GetFileName($latestOVJ)
        Write-Host "Downloading",([System.IO.Path]::GetFileName($latestOVJ)),"..." -nonewline # e.g. Downloading OpenVnmrJ_3.1_DDR.zip...
        dlFile "$latestOVJ" "$ovjInstaller"
        Write-Host "......[Done]" # e.g. Downloading OpenVnmrJ_3.2_DDR.zip.........[Done]
        echo ""
    }
    # Extract installer
    if ([System.IO.Path]::GetExtension($ovjInstaller) -eq '.zip') {
        $cmd='unzip \$(wslpath \"'+"$ovjInstaller"+'\")'
    }
    elseif ([System.IO.Path]::GetExtension($ovjInstaller) -eq '.tar') {
        $cmd='tar -xvf \$(wslpath \"'+"$ovjInstaller"+'\")' 
