# Create Shortcuts
function createShorcuts([string]$distribution) {
    if ($desktopCheckBox.Checked -Or $startMenuCheckBox.Checked) {
        # Create icon for shortcut
        $iconDir="$([Environment]::GetFolderPath('LocalApplicationData'))\OpenVnmrJ"
        if (-not(Test-Path $iconDir)) { [void](New-Item $iconDir -ItemType Directory) }
        $iconFile="$iconDir\OVJ.ico"
        $IconBase64=""
        [byte[]]$Bytes = [convert]::FromBase64String($IconBase64)
        [System.IO.File]::WriteAllBytes($iconFile,$Bytes)

        # Initialize Shortcuts
        $WScriptObj = New-Object -ComObject ("WScript.Shell")

        $desktopShortcut="$([Environment]::GetFolderPath('Desktop'))\OpenVnmrJ.lnk"
        $shortcutD = $WscriptObj.CreateShortcut($desktopShortcut)
        $desktopShortcutAdmin="$([Environment]::GetFolderPath('Desktop'))\OpenVnmrJ Admin.lnk"
        $shortcutAdminD = $WscriptObj.CreateShortcut($desktopShortcutAdmin)
        $startShortcut="$([Environment]::GetFolderPath('ApplicationData'))\Microsoft\Windows\Start Menu\Programs\OpenVnmrJ.lnk"
        $shortcutS = $WscriptObj.CreateShortcut($startShortcut)
        $startShortcutAdmin="$([Environment]::GetFolderPath('ApplicationData'))\Microsoft\Windows\Start Menu\Programs\OpenVnmrJ Admin.lnk"
        $shortcutAdminS = $WscriptObj.CreateShortcut($startShortcutAdmin)

        $shortcutD.IconLocation = "$iconFile"
        $shortcutD.WindowStyle = 7
        $shortcutD.WorkingDirectory = "$([Environment]::GetFolderPath('UserProfile'))"
        $shortcutAdminD.IconLocation = "$iconFile"
        $shortcutAdminD.WindowStyle = 7
        $shortcutAdminD.WorkingDirectory = "$([Environment]::GetFolderPath('UserProfile'))"

        $shortcutS.IconLocation = "$iconFile"
        $shortcutS.WindowStyle = 7
        $shortcutS.WorkingDirectory = "$([Environment]::GetFolderPath('UserProfile'))"
        $shortcutAdminS.IconLocation = "$iconFile"
        $shortcutAdminS.WindowStyle = 7
        $shortcutAdminS.WorkingDirectory = "$([Environment]::GetFolderPath('UserProfile'))"

        if ($winBuild -lt $wslgBuild) { # non-WSLg Windows 10
            # Build batch file to launch VcXsrv if needed and then launch OpenVnmrJ
            $batFile="$iconDir\vnmrj.bat"
            'tasklist /fi "IMAGENAME eq VcXsrv.exe" /fo csv 2>NUL | find /I "vcxsrv.exe">NUL' | Out-File -FilePath $batFile -Encoding ascii
            'if "%ERRORLEVEL%"=="1" start /B /D "C:\Program Files\VcXsrv" vcxsrv.exe :0 -multiwindow -clipboard -wgl -once -auth "'+$([Environment]::GetFolderPath('UserProfile'))+'\.Xauthority"' | Out-File -FilePath $batFile -Append -Encoding ascii
            'C:\WINDOWS\system32\wsl.exe -d '+$distribution+' /bin/bash -lic "vnmrj; sleep 3"' | Out-File -FilePath $batFile -Append -Encoding ascii
            $batFileAdmin="$iconDir\vnmrjadmin.bat"
            'tasklist /fi "IMAGENAME eq VcXsrv.exe" /fo csv 2>NUL | find /I "vcxsrv.exe">NUL' | Out-File -FilePath $batFileAdmin -Encoding ascii
            'if "%ERRORLEVEL%"=="1" start /B /D "C:\Program Files\VcXsrv" vcxsrv.exe :0 -multiwindow -clipboard -wgl -once -auth "'+$([Environment]::GetFolderPath('UserProfile'))+'\.Xauthority"' | Out-File -FilePath $batFileAdmin -Append -Encoding ascii
            'C:\WINDOWS\system32\wsl.exe -d '+$distribution+' -u vnmr1 /bin/bash -lic "vnmrj admin; sleep 3"' | Out-File -FilePath $batFileAdmin -Append -Encoding ascii

            # Set shortcut to launch batch file
            $shortcutD.TargetPath = $batFile
            $shortcutAdminD.TargetPath = $batFileAdmin
            $shortcutS.TargetPath = $batFile
            $shortcutAdminS.TargetPath = $batFileAdmin
        }
        else { # WSLg-based Windows 10/11 - launch OpenVnmrJ directly
            $shortcutD.TargetPath = 'C:\WINDOWS\system32\wsl.exe'
	    $shortcutD.Arguments = '-d '+$distribution+' /bin/bash -lic "vnmrj; sleep 3"'
            $shortcutAdminD.TargetPath = 'C:\WINDOWS\system32\wsl.exe'
	    $shortcutAdminD.Arguments = '-d '+$distribution+' -u vnmr1 /bin/bash -lic "vnmrj admin; sleep 3"'
            $shortcutS.TargetPath = 'C:\WINDOWS\system32\wsl.exe'
	    $shortcutS.Arguments = '-d '+$distribution+' /bin/bash -lic "vnmrj; sleep 3"'
            $shortcutAdminS.TargetPath = 'C:\WINDOWS\system32\wsl.exe'
	    $shortcutAdminS.Arguments = '-d '+$distribution+' -u vnmr1 /bin/bash -lic "vnmrj admin; sleep 3"'
        }

        if ($desktopCheckBox.Checked) {
            echo "Creating Desktop shortcuts"
            $shortcutD.Save()
            $shortcutAdminD.Save()
            echo ""
        }

        if ($startMenuCheckBox.Checked) {
            echo "Adding shortcuts to Start Menu"
            $shortcutS.Save()
            $shortcutAdminS.Save()
            echo ""
        }
    }
}

function installController {
    # Create working directory if needed
    if (-not(Test-Path $ovjWSLfolder)) { [void](New-Item $ovjWSLfolder -ItemType Directory) }

    # Install X server for Windows 10
    if ( $winBuild -lt $wslgBuild ) {installVcXsrv}

    # Update WSL to convert to WSL 2 on Windows 10 and install WSLg where supported
    echo "Updating WSL"
    wsl --update
    wsl --shutdown

    # Determine if Ubuntu or Alma Linux is already installed
    $instU = 1 # Install flag
    $ubuntus = @((wsl -l) -replace "`0" | Select-String Ubuntu) # -replace "`0" due to wsl UTF-16LE-encoded output
    $almas = @((wsl -l) -replace "`0" | Select-String -Pattern "(Alma)") # -replace "`0" due to wsl UTF-16LE-encoded output
    $step = 0
    $validDistros = @()
    foreach ($dist in $ubuntus) { # Remove older, unsupported Ubuntu versions from list
        if ( $dist -match "-" ) { 
            if ((($dist -split "\.")[0] -replace "Ubuntu-") -ge $minDist) {
                $validDistros += $dist
            }
        } else {$validDistros += $dist } # Plain Ubuntu (no version label) case
    }
    foreach ($dist in $almas) { # Remove older, unsupported Alma versions from list
        if ( $dist -match "-") { 
            if (($dist -replace "Alma.*-") -ge $minAlma) {
                $validDistros += $dist
            }
        }
    }
    $step = 0
    echo ""
    echo "╔═════════════════════════════════════════╗"
    echo "║ Select a supported distro for OpenVnmrJ ║"
    echo "╚═════════════════════════════════════════╝"
    echo ""
    if ($validDistros) {
        echo "  Distros already present on this system"
        echo "-------------------------------------------"
        foreach ($dist in $validDistros) {
            echo "   [$step] $dist"
            $validDistros[$step] = $($dist -replace '\(.*').Trim()
            $step++
        }
        echo ""
        echo "     Or install a new supported distro"
    } else {
        echo "        Install a supported distro"
    }

    echo "-------------------------------------------"
    $notInstalled = $supportedWSL| ? {$_ -notin $validDistros}
    foreach ($dist in $notInstalled) {
        echo "   [$step] $dist"
        $step++
    }
    echo ""
    do { # Verify number input
        $whichDistro = Read-Host -Prompt "  Select distribution to use"
        if ($whichDistro -eq '') {$whichDistro = $step + 1}
        $value = $whichDistro -as [Int]
        $ok = $(($value -ne $NULL) -And ($value -lt $step))
        if (-not $ok) {Write-Host ""; Write-Host "! Enter digit less than $step only"}
    } until ($ok)
    if ($whichDistro -ge $validDistros.Length) {
        $installDist = $notInstalled[$whichDistro-$validDistros.Length]
    } else {
        $instU = 0 # Use an installed distribution
        $installDist = $validDistros[$whichDistro]
    }

    echo ""
    $setDefault = Read-Host -Prompt "  Do you want to make $installDist the default WSL distribution [y/N]?"

    if ($instU) { installUbuntu($installDist) }
    setupUbuntu($installDist)
    installOVJ($installDist)
    createShorcuts($installDist)
    # Restore wsl default version, if was 1 before running this script
    if ($revertWSLdefault) {
        echo "Restoring default WSL install version to 1"
        wsl --set-default-version 1
    }
    if ($setDefault -ieq "y" -Or $setDefault -ieq "yes" ) {
        echo "";echo "Setting $installDist as the default WSL distribution"
        wsl -s $installDist
    }
    echo "";echo "***  Installation Complete!  ***";echo ""
    Read-Host -Prompt "Press [Enter] to close this window"
}

if ( $mainWindow.DialogResult -match 'OK' ) {
    installController
}
else {Exit 1}
